import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useNav(); // Use the useHistory hook

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            // Example of making a POST request to your backend for authentication
            const response = await fetch('http://localhost:8080/user/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });
    
            if (!response.ok) {
                throw new Error('Login failed');
            }
    
            // Handle successful login, e.g., store token in localStorage, redirect user, etc.
            const data = await response.json();
            // Example: store token in localStorage
            localStorage.setItem('token', data.token);
    
            // Redirect user to another page upon successful login
            // Example using react-router-dom history
             navigate('/dashboard');// Ensure you have history object from react-router-dom
    
        } catch (error) {
            alert('Login failed. Please check your credentials and try again.');
            console.error('Login Error:', error);
        }
      
        // Add your login logic here
    };

    return (
        <div className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh' }}>
            <div className="col-md-4">
                <div className="card">
                    <div className="card-body">
                        <h2 className="card-title text-center">Login</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="username">Username</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="username"
                                    value={username}
                                    onChange={handleUsernameChange}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input
                                    type="password"
                                    className="form-control"
                                    id="password"
                                    value={password}
                                    onChange={handlePasswordChange}
                                />
                            </div>
                            <div className='m-1'>

                            <button type="submit" className="btn btn-primary btn-block">Login </button>
                            
                            </div>
                            <div>
                                <h6>If you new user means plz..</h6>
                            <Link to="/register">Register</Link>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
