import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container, Row, Col, Card } from 'react-bootstrap';
import { useLocation } from 'react-router-dom';

const Home = () => {
    const [showProfile, setShowProfile] = useState(false);
    const [userData, setUserData] = useState([]); // State to hold user data array
    const [username, setUsername] = useState(''); // State to hold current user's username
    const location = useLocation();

    useEffect(() => {
        if (location.state && location.state.userData) {
            setUserData(location.state.userData);
        }
        if (location.state && location.state.username) {
            setUsername(location.state.username);
        }
    }, [location]);

    const toggleProfile = () => {
        setShowProfile(!showProfile);
    };

    return (
        <div>
            <header className='bg-light p-3'>
                <Container>
                    <Row className='align-items-center'>
                        <Col>
                            <Navbar expand="lg" className="justify-content-between">
                                <Nav className="mx-auto">
                                    <Navbar.Brand className='text-center'>Chat App</Navbar.Brand>
                                </Nav>
                                <Nav className="ml-auto">
                                    <Nav.Link href="#" onClick={toggleProfile}>
                                        <i className="bi bi-person-circle"></i> Profile
                                    </Nav.Link>
                                </Nav>
                            </Navbar>
                        </Col>
                    </Row>
                </Container>
            </header>
            <Container className='mt-4'>
                <Row>
                    <Col>
                        <div>Content of your home page goes here...</div>
                    </Col>
                    {userData.length > 0 && (
                        <Col md={3} className='d-flex flex-column align-items-end'>
                            <Card className='w-100'>
                                <Card.Body>
                                    <Card.Title>User Relationships</Card.Title>
                                    {userData.map((relationship) => (
                                        <select key={relationship.id}>
                                            <Card.Text>
                                              {relationship.userId === username
                                                    ? `User: ${relationship.frdId}`
                                                    : `User: ${relationship.userId}`}

                                            </Card.Text>
                                        </select>
                                    ))}
                                </Card.Body>
                            </Card>
                        </Col>
                    )}
                </Row>
            </Container>
        </div>
    );
};

export default Home;
