import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; 

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Use the useHistory hook

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = async(e) => {
        await axios.post('http://localhost:8080/user/login', {
            username: username,
            password: password
        })
            /**
             * if the response are get from the backend "If the sending the data to the frontend it will store in the response"
             */ 
            .then(response => {
                console.log(response.data);
                //setUser(response.data);

            
                /**
                 * this method @navigate is pass only the path which loaction we want to redrict 
                 * I not use this method because i want to pass the path and variable ;
                 */

                // navigate('/home');



                /**
                 * This @navigate ('/home', { state: { user: response.data } });
                 * pass the path and data to corresponding location
                 */
                // navigate('/home', { state: { user: response.data } });
                navigate('/');




                //setLoggedIn(true); // Assuming you have a state variable to track login status

            })
            /**
             * If any error will come in place it will catch and show the message
             */
            .catch(error => {
                console.error("Error during login:", error);
                setUsername('');
                setPassword('');
                //alert(error.response.data);
            });
      
        // Add your login logic here
    };

    return (
        <div className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh' }}>
            <div className="col-md-4">
                <div className="card">
                    <div className="card-body">
                        <h2 className="card-title text-center">Login</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="username">Username</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    id="username"
                                    value={username}
                                    onChange={handleUsernameChange}
                                />
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input
                                    type="password"
                                    className="form-control"
                                    id="password"
                                    value={password}
                                    onChange={handlePasswordChange}
                                />
                            </div>
                            <div className='m-1'>

                            <button type="submit" className="btn btn-primary btn-block">Login </button>
                            
                            </div>
                            <div>
                                <h6>If you new user means plz..</h6>
                            <Link to="/register">Register</Link>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
